body.script:
 if ( jQuery('.field-prgf-head-content-buttons .field-link-target').text().length > 0 ) {
  jQuery( ".field-link-target" ).parent( ".field-prgf-head-content-buttons" ).addClass('targetAttribute');
 }
 if ( jQuery('.field-prgf-head-content-buttons .field-link-attribute').text().length > 0 ) {
  jQuery( ".field-link-attribute" ).parent( ".field-prgf-head-content-buttons" ).addClass('targetAttribute');
 }

if ( jQuery('.field-fblog-posts .field-link-target').text().length > 0 ) {
  jQuery( ".field-link-target" ).parent( ".field-fblog-posts" ).addClass('targetAttribute');
 }
 if ( jQuery('.field-fblog-posts .blog-heading').text().length > 0 ) {
  jQuery( ".blog-heading" ).parent( ".field-fblog-posts" ).addClass('targetAttribute');
 }

 jQuery('.btext').each(
function() {
    if (!jQuery(this).text()) {
        jQuery(this).parentsUntil().addClass('ybtn');
    }
});
 jQuery('.targetAttribute').find('a').attr('target','_blank');
 jQuery('.field-fblog-posts').find('a').attr('target','_blank');
 jQuery(".site-alert__dismiss").attr("target", "");
