    if($(this).find(':first-child').children('.text').html() == 'Community'){
      $('.portal-links ul li:nth-child(2)').append($(this).find('.category ul').addClass('portal-nav-links').hide());
      $(this).hide(1);
    }
    if($(this).find(':first-child').children('.text').html() == 'Staff'){
      $('.portal-links ul li:nth-child(3)').append($(this).find('.category ul').addClass('portal-nav-links').hide());
      $(this).hide(1);
    }

  });

