$(document).ready(function(e) {
  var slide = $('#em-alerts');
  $(slide).show();
  $('a#close-btn').attr('title', 'Hide Emergency Alert');
  
  $('#close-btn').click(function(e){

    $('.alertInner').slideToggle("slow").toggleClass('opened');

    var isVisible = $('.alertInner').is(".opened");

    if (isVisible === true ){
       // Hide
       $(this).find($(".fa")).removeClass('fa-times').addClass('fa-exclamation-circle');
       $('a#close-btn').attr('title', 'Show Emergency Alert');
     }

     else {
       // Show
       $(this).find($(".fa")).removeClass('fa-exclamation-circle').addClass('fa-times');
       $('a#close-btn').attr('title', 'Hide Emergency Alert');
     }

     e.preventDefault();
   });
  
  
  if($("form[action*='&action=design']").length){
   $(slide).hide();
 }

});

