	function sortSchools(){

        $(function() {
            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 6063,
                'cache': false,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';
					var csElemSchools = [], csK8Schools = [], csMiddleSchools = [], csHighSchools = [], csPreKSchools = [], csOtherSchools = [];

                    for(var i in schoolList) {
                    	//console.log(schoolList[i].schoolType.toLowerCase());

                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            //schools += "
" + schoolList[i].schoolName + "";
                            var csSchoolName = schoolList[i].schoolName, csSchoolURL = schoolList[i].schoolUrl;
                            var csPackedSchool = [csSchoolName, csSchoolURL];
                            //-- ELEMENTARY SCHOOL
                            if(schoolList[i].schoolType.toLowerCase() == "elementary") {
                                csElemSchools.push(csPackedSchool);
                            }
                            //-- K-8 SCHOOL
                            else if(schoolList[i].schoolType.toLowerCase() == "k-8") {
                                csK8Schools.push(csPackedSchool);
                            }
                            //-- MIDDLE SCHOOL
                            else if(schoolList[i].schoolType.toLowerCase() == "middle") {
                                csMiddleSchools.push(csPackedSchool);
                            }
                            //-- HIGH SCHOOL
                            else if(schoolList[i].schoolType.toLowerCase() == "high") {
                                csHighSchools.push(csPackedSchool);
                            }
                            //-- PRE-K SCHOOL
                            else if(schoolList[i].schoolType.toLowerCase() == "prek") {
                                csPreKSchools.push(csPackedSchool);
                            }
                            //-- Other
                            else {
                                // DONT INCLUDE PARENTS, STUDENTS, STAFF, ESPANOL, OR ARABIC SITES
                                if(schoolList[i].schoolType.toLowerCase() != "parents") {
                                    csOtherSchools.push(csPackedSchool);
                                }
                            }
                        }

                    };//End each

                    var elementarySchools = "";
                    for(var e in csElemSchools) {
                        elementarySchools += "1," + csElemSchools[e][0] + "," + csElemSchools[e][1] + ",_blank;";
                    }
                    // BUILD K8 SCHOOL LIST
                    var k8Schools = "";
                    for(var a in csK8Schools) {
                        k8Schools += "2," + csK8Schools[a][0] + "," + csK8Schools[a][1] + ",_blank;";
                    }
                    // BUILD MIDDLE SCHOOL LIST
                    var middleSchools = "";
                    for(var m in csMiddleSchools) {
                        middleSchools += "3," + csMiddleSchools[m][0] + "," + csMiddleSchools[m][1] + ",_blank;";
                    }
                    // BUILD HIGH SCHOOL LIST
                    var highSchools = "";
                    for(var a in csHighSchools) {
                        highSchools += "4," + csHighSchools[a][0] + "," + csHighSchools[a][1] + ",_blank;";
                    }
                    // BUILD PRE-K SCHOOL LIST
                    var preKSchools = "";
                    for(var a in csPreKSchools) {
                        preKSchools += "5," + csPreKSchools[a][0] + "," + csPreKSchools[a][1] + ",_blank;";
                    }
                    // BUILD SUMMER SCHOOL LIST
                    var otherSchools = "";
                    for(var a in csOtherSchools) {
                        otherSchools += "6," + csOtherSchools[a][0] + "," + csOtherSchools[a][1] + ",_blank;";
                    }


                    function buildSchoolList(csElemSchools, csK8Schools, csMiddleSchools, csHighSchools, csPreKSchools, csOtherSchools) {
                        // BUILD ELEMENTARY SCHOOL LIST
                        var fullSchoolList = '
';
                        if(csElemSchools.length > 0){
                            fullSchoolList += '
'+
                                        '
Elementary Schools'+
                                        '';
                            var n = 1;
                            for(var i = 0; i < csElemSchools.length; i++) {
                                if(n == 1){
                                    fullSchoolList += "
" 
                                        + csElemSchools[i][0] + "";
                                } else if( csElemSchools.length == n){
                                    fullSchoolList += "
" 
                                        + csElemSchools[i][0] + "";
                                } else {
                                    fullSchoolList += "
"
                                        + csElemSchools[i][0] + "";
                                }
                                n++;
                            }

                            fullSchoolList += '';
                        }
                        if(csK8Schools.length > 0 || csMiddleSchools.length > 0 || csHighSchools.length > 0) {
                            fullSchoolList += '
';
                            if(csK8Schools.length > 0){
                                // BUILD K8 SCHOOL LIST
                                fullSchoolList += '
' +
                                    '
K-8 Schools' +
                                    '';
                                var n = 1;
                                for(var a in csK8Schools) {
                                    if((a + 1) == 1){
                                        fullSchoolList += '
' +
                                            csK8Schools[a][0] + '';

                                    } else if(a == (csK8Schools.length - 1)){
                                        fullSchoolList += '
' +
                                            csK8Schools[a][0] + '';
                                    } else {
                                        fullSchoolList += '
' +
                                            csK8Schools[a][0] + '';
                                    }
                                    n++;
                                }
                                fullSchoolList += '';
                            }
                            if(csMiddleSchools.length > 0){
                                // BUILD MIDDLE SCHOOL LIST
                                fullSchoolList += '
' +
                                    '
Middle Schools' +
                                    '';
                                for(var a in csMiddleSchools) {
                                    if(n == 1){
                                        fullSchoolList += '
' +
                                            csMiddleSchools[a][0] + '';
                                    } else if(a == (csMiddleSchools.length - 1)){
                                        fullSchoolList += '
' +
                                            csMiddleSchools[a][0] + '';
                                    } else {
                                        fullSchoolList += '
' +
                                            csMiddleSchools[a][0] + '';
                                    }
                                    n++;
                                }
                                fullSchoolList += '';
                            }
                            if(csHighSchools.length > 0){
                                // BUILD HIGH SCHOOL LIST
                                fullSchoolList += '
' +
                                    '
High Schools' +
                                    '';
                                for(var a in csMiddleSchools) {
                                    if((a + 1) == 1){
                                        fullSchoolList += '
' +
                                            csHighSchools[a][0] + '';
                                    } else if(a == (csMiddleSchools.length - 1)){
                                        fullSchoolList += '
' +
                                            csHighSchools[a][0] + '';
                                    } else {
                                        fullSchoolList += '
' +
                                            csHighSchools[a][0] + '';
                                    }
                                    n++;
                                }
                                fullSchoolList += '';
                            }
                            fullSchoolList += '';
                        }
                        if(csPreKSchools.length > 0 || csOtherSchools.length > 0){
                            fullSchoolList += '
';
                            if(csPreKSchools.length > 0){
                                // BUILD PREK SCHOOL LIST
                                fullSchoolList += '
' +
                                    '
Pre-K and Extended Day' +
                                    '';
                                var n = 1;
                                for(var a in csPreKSchools) {
                                    if(n == 1){
                                        fullSchoolList += '
' +
                                            csPreKSchools[a][0] + '';
                                    } else if(a == (csPreKSchools.length - 1)){
                                        fullSchoolList += '
' +
                                            csPreKSchools[a][0] + '';
                                    } else {
                                        fullSchoolList += '
' +
                                            csPreKSchools[a][0] + '';
                                    }
                                    n++;
                                }
                                fullSchoolList += '';
                            }
                            if(csOtherSchools.length > 0){
                                // BUILD HOME EDUCATION SCHOOL LIST
                                fullSchoolList += '
' +
                                    '
Home Education' +
                                    '';
                                for(var a in csOtherSchools) {
                                    if(n == 1 && csOtherSchools[a][1] != undefined){
                                        fullSchoolList += '
' +
                                            csOtherSchools[a][0] + '';
                                    //} else if(a == (csOtherSchools.length - 1 && csOtherSchools[a][1] != undefined)){
                                    //    fullSchoolList += '
' +
                                    //        csOtherSchools[a][0] + '';
                                    } else if(csOtherSchools[a][1] != undefined) {
                                        fullSchoolList += '
' +
                                            csOtherSchools[a][0] + '';
                                    }
                                    n++;
                                }
                                fullSchoolList += '';
                             }
                            fullSchoolList += '';
                        }

                        fullSchoolList += '';
                        //-- ADD EVERYTHING
                        $(".gb-custom-dropdown.schools .sw-dropdown").html(fullSchoolList);
                    }

                    //$('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                    buildSchoolList(csElemSchools, csK8Schools, csMiddleSchools, csHighSchools, csPreKSchools, csOtherSchools);
                }
            });
        });


        $('.schools .sw-dropdown a').off('keydown').keydown(function(e){
            var c = e.which,
            school = $(this).data('school'),
            column = $(this).data('column');
            if(c == kUp || c == kDown || c == kLeft || c == kRight){
                e.preventDefault();
                if(c == kUp && school == '1'){
                    $('.schools').focus();
                } else if(c == kUp){
                    $('*[data-school="'+ (school - 1) +'"][data-column="'+ column +'"]').focus();
                } else if(c == kDown){
                    $('*[data-school="'+ (school + 1) +'"][data-column="'+ column +'"]').focus();
                } else if(c == kLeft){
                    $('*[data-school="'+ school +'"][data-column="'+ (column - 1) +'"]').focus();
                } else if(c == kRight){
                    $('*[data-school="'+ school +'"][data-column="'+ (column + 1) +'"]').focus();
                }
            }
        });

