	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }
		if(isMSIE11){ $("body").addClass("msie11"); }
		$("body").addClass(navigator.platform + " " + browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){ 
			calendarListView();
        	resize();
		});


		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
			"breakPoint"		: 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"	: "#sp-content .sp.column.one",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"			: "4"
		});

		useChannelForSection();
		dynamicContent();
		mediaRotator();	
        siteSpecificSearch(); // Added by Adam 11.15.16


	});	

    function siteSpecificSearch(){
    	var siteIdString = "4";
        var googleSearchID = "010382873129093968663:nq0amy7kote";


        $("#sw-search-button").removeAttr("onclick");
        $("#gb-custom-search").append(''+
                    ''+
                    '');

        $("#sw-search-button").on("click", function(e){
                    e.preventDefault();
                    //window.location.href = "https://www.google.com/cse?cx="+$('input.cx').val()+"&hq="+$('input.hq').val()+"&ie="+$('input.ie').val()+"&q="+$('#sw-search-input').val()+"#gsc.tab=0&gsc.q="+$('#sw-search-input').val()+"&gsc.page=1";
                    window.location.href = "https://www.google.com/cse?cx="+googleSearchID+"&ie=UTF-8&q="+$('#sw-search-input').val() + "+more:pagemap:document-siteid:4";
        });

        $("body").on("click", "#rs-menu-search-button", function(e){
                    e.preventDefault();
                    //window.location.href = "https://www.google.com/cse?cx="+$('input.cx').val()+"&hq="+$('input.hq').val()+"&ie="+$('input.ie').val()+"&q="+$('#rs-menu-search-input').val()+"#gsc.tab=0&gsc.q="+$('#rs-menu-search-input').val()+"&gsc.page=1";
        			window.location.href = "https://www.google.com/cse?cx="+googleSearchID+"&ie=UTF-8&q="+$('#rs-menu-search-input').val() + "+more:pagemap:document-siteid:4";
        });

        $("body").on("keyup", "#sw-search-input, #rs-menu-search-input", function(e) {
            if (e.keyCode == 13) {
                e.preventDefault();
                //window.location.href = "https://www.google.com/cse?cx="+$('input.cx').val()+"&hq="+$('input.hq').val()+"&ie="+$('input.ie').val()+"&q="+$(this).val()+"#gsc.tab=0&gsc.q="+$(this).val()+"&gsc.page=1";
                window.location.href = "https://www.google.com/cse?cx="+googleSearchID+"&ie=UTF-8&q="+$(this).val() + "+more:pagemap:document-siteid:4";
                e.stopImmediatePropagation();
            }
        });
    }

    function resize(){
		var vpw = $(window).width();
        if(vpw < 1024){
        	$('.hp .ui-widget-header').each(function(k, v){
                if($('.arrow-icon', v).size() == 0) {
                    $(v).append(arrowSVG);
                }
            });
            $('.gb-sponsor-inner').removeClass('cs-jc').addClass('cs-jsa');
            $('#gb-header-left').removeClass('cs-jc');
            $('.gb-footer-numbers').removeClass('cs-fcc cs-jc').addClass('cs-fc');
            if(vpw < 768){
                if(moveFooter == 0){
                	$('#gb-footer').prepend($('.gb.footer.one'));
                    moveFooter++;
                }
                $('.gb-sponsor-inner').removeClass('cs-jsa').addClass('cs-jc');
                $('#gb-header-left').removeClass('cs-jc');
                    if($('.shore').size() > 0){
                    	$('#gb-header-left').removeClass('cs-fcc').addClass('cs-fc').removeClass('cs-jc').addClass('cs-jsb');
                    }
                $('.gb-footer-numbers').addClass('cs-fcc cs-jc').removeClass('cs-fc');
                if(vpw < 640){
                	$('#gb-header-left').addClass('cs-jc');
                    if($('.shore').size() > 0){
                    	$('#gb-header-left').addClass('cs-fcc').removeClass('cs-fc').removeClass('cs-jsb');
                    }
                }
            }
            appView();
        } else {
			appViewReset();
            $('.ui-widget-header .arrow-icon').remove();
            $('#gb-header-left').removeClass('cs-jc');
            $('.gb-sponsor-inner').removeClass('cs-jc').addClass('cs-jsa');
            $('.gb-footer-numbers').removeClass('cs-fcc cs-jc').addClass('cs-fc');
        }
    };

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				appViewReset();
			break;
			case "768":
				appView();
			break;
			case "640":
				appView();
			break;
			case "480":
				appView();
			break;
			case "320":
				appView();
			break;
		}
	}

	function mediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth" 			: 710,
		        	"imageHeight" 			: 380,
		        	"appHeader" 			: "no",
		        	"titles" 			: "yes",
		        	"captions" 			: "yes",
		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "no",
		        	"mainControls" 			: "no",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "Read More",
		        	"descMoreLink" 			: "no",
		        	"descMoreLinkText" 		: "Read More",
		        	"bullets" 				: "yes",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "no", // image dimensions work well at 1500x915
		        	"fullScreenBreakpoints"   	: ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "fade", // fade or slide
		        	"transitionSpeed" 		: 2.5,
		        	"transitionDelay" 		: 5,
					"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() { },
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() { },
		        	"onTransitionEnd" 		: function() { 
                    	var vpw = $(window).width();
                    	if(vpw >= 1024){
                            var descH = $('#hp-slideshow .rs-gallery-desc').height(),
                                position = 380 - descH,
                                position = position / 2;
                            $('#hp-slideshow .rs-gallery-desc').css('top', position + 'px');
                        }
                    },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() { 
                    	var vpw = $(window).width();
                    	if(vpw >= 1024){
                            var descH = $('#hp-slideshow .rs-gallery-desc').height(),
                                position = 380 - descH,
                                position = position / 2;
                            $('#hp-slideshow .rs-gallery-desc').css('top', position + 'px');
                        }
                    }
			});
		}
	}

	function appView() {
		if(!appViewSet) {
        	$('#hp-tab-section div.ui-widget-detail, #hp-content-outer div.ui-widget-detail').hide();

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$("#hp-content-outer div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$("#hp-tab-section div.ui-widget-header, #hp-content-outer div.ui-widget-header").click(function(){
				if($(this).parent().hasClass('active')){
                    $(this).parent().removeClass("active");
                    $(this).siblings('.ui-widget-detail').slideUp();
                } else {
                    $("#hp-tab-section div.ui-widget.app, #hp-content-outer div.ui-widget.app").removeClass("active");
                    $('#hp-tab-section .ui-widget.app .ui-widget-detail, #hp-content-outer div.ui-widget.app .ui-widget-detail').slideUp();
                    $(this).parent().addClass("active");
                    $(this).siblings('.ui-widget-detail').slideDown();
                }
			});

			appViewSet = 1;
		}
	}

	function appViewReset() {
		if(appViewSet) {
			$("#hp-tab-section div.ui-widget-header, #hp-content-outer div.ui-widget-header").unbind("click");
			$('#hp-tab-section div.ui-widget-detail, #hp-content-outer div.ui-widget-detail').show();
			appViewSet = 0;
		}
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Community");
		}
        }

