			$("
" + manageBtn + "").prependTo("#sw-myaccount-list");
			$(".sw-mystart-button.manage").hide();
		}

        //-- Move Search
        $('#gb-custom-search').append($('#sw-mystart-search').html());
        $('#sw-mystart-search, #sw-search-button').remove();
        $('#sw-search-input').attr('aria-hidden', 'true').attr('tabindex', '-1').addClass('acc-hidden');

        //-- Search button functionality

        $('button.gb-custom-button.search').click(function(){
        	var p = $('#gb-custom-search').position(),
            	w = p.left - 45;
            if($(this).hasClass('open')){
                $(this).removeClass('open').attr('aria-label', 'Open Search');
                $('#sw-search-input').addClass('acc-hidden').width('0px').attr('aria-hidden', 'true').attr('tabindex', '-1').css('background', 'transparent');
            } else {
                $(this).addClass('open').attr('aria-label', 'Close Search');
                $('#sw-search-input').removeClass('acc-hidden').width( w + 'px').attr('aria-hidden', 'false').attr('tabindex', '0').css('background', 'rgba( 103,111,124, 0.10)');
            }
        }).keyup(function(e){
        	var c = e.which;
            if(c == kEnter){
                e.preventDefault();
                if($(this).hasClass('open')){
                	$('#sw-search-input').focus();
                }
            }
        }).keydown(function(e){
        	var c = e.which;
            if(c == kSpace){
                e.preventDefault();
                $(this).click();
                if($(this).hasClass('open')){
                	$('#sw-search-input').focus();
                }
            }
        });
        $('#sw-search-input').blur(function(){
                $(this).width('0px').attr('aria-hidden', 'true').attr('tabindex', '-1').css('background', 'transparent');
                $('button.gb-custom-button.search').removeClass('open').attr('aria-label', 'Open Search');
        });

        //-- Move Passkeys
