                $('.sw-dropdown a', this).attr('tabindex', '-1');
            } else if($(this).hasClass('open') && $(this).hasClass('schools')){
            	sHOut();
            } else {
            	$('.gb-custom-dropdown').removeClass('open');
                $('.gb-custom-dropdown .sw-dropdown').slideUp();
                $('.gb-custom-dropdown .sw-dropdown').stop().slideUp().attr('aria-hidden', 'true');
            	$(this).addClass('open');
                $('.sw-dropdown', this).stop().slideDown().attr('aria-hidden', 'false');
                $('.sw-dropdown a', this).attr('tabindex', '-1');
                $('.sw-dropdown a', this).first().addClass('first');
