        	if(c == kEnter || c == kSpace || (c == kDown && !($(this).hasClass('inner')))){
            	if(!($(this).hasClass('open'))){
                	$(this).click();
                }
                $(this).addClass('inner');
                $('.sw-dropdown a', this).first().focus();
                e.preventDefault();
            }
        }).blur(function(){
        	if(!($(this).hasClass('inner')) && $(this).hasClass('open')){
            	$(this).click();
            }
        }).focus(function(){
        	if($(this).hasClass('inner')){
            	$(this).removeClass('open').removeClass('inner');
                $('.sw-dropdown', this).stop().slideUp().attr('aria-hidden', 'true');
                $('.sw-dropdown a', this).attr('tabindex', '-1');
