            	next = $(this).parent().next().children(),
                prev = $(this).parent().prev().children();
            if((c == kDown && !($(this).hasClass('schools')))|| (c == kUp && !($(this).hasClass('schools')))){
                e.preventDefault();
                if(c == kUp && $(this).hasClass('first')){
                    $(this).closest('.inner').focus();
                }
                if(c == kDown){
                    next.focus();
                } else {
                    prev.focus();
                }
            }
        });
