		var dt_pgupdate = new Date(Date.parse("Thu, 09 Apr 2020 19:07:44 GMT"));
		var dt_pgload = new Date();

		function InitPageAgeTest(pgupdate_timestamp)
		{
			var dt_pgdiff = (dt_pgload - pgupdate_timestamp) / (1000*60); // in minutes

			if (dt_pgdiff > 60)
			{
				if (document.all) // IE 4+
				{
					divPageAge = document.all['PageAgeWarning'];
				}
				else if (document.getElementById) // Mozilla and other browsers supporting the W3C DOM
				{
					divPageAge = document.getElementById('PageAgeWarning');
				}

				divPageAge.style.display = 'inline';
			}
		}

            function initializeMap() {    
              var map;
              require([
                "esri/map", "esri/dijit/BasemapToggle", "esri/layers/KMLLayer", "dojo/parser", "dojo/dom-style",
                "dijit/layout/BorderContainer", "dijit/layout/ContentPane", "dojo/domReady!"
              ], function(
                Map, BasemapToggle, KMLLayer,
                parser, domStyle
              ) {
                map = new Map("map", {
                  basemap: "topo",
                  center: [-121.73742499999990000, 48.035097999999990000],
                  zoom: 9
                });

                var toggle = new BasemapToggle({
                    map: map,
                    basemap: "satellite",
                    showAttribution: false
                }, "BasemapToggle");
                toggle.startup();
                
                var kmlUrl = "http://publicworks.snoco.org/RdClosures/SnocoRdClosures4GMaps.kml";
                var kml = new KMLLayer(kmlUrl);
                map.addLayer(kml);
                map.infoWindow.resize(400,200);
              });
            }

