			function Watermark(obj, txt) {

				if (jQuery.trim($(obj).val()) == "" || jQuery.trim($(obj).val()) == txt) {
					$(obj).val(txt);
					$(obj).addClass("watermark");
				}

				$(obj).focus(function () {
					if ($(this).val() == txt) {
						$(this).val("");
					}
					$(this).removeClass("watermark");
				});

				$(obj).blur(function () {
					if ($(this).val().trim() == "") {
						$(this).val(txt);
						$(this).addClass("watermark");
					}
				});

				return obj;
			}
