	function switchDip_714782(num,pause){
		clearTimeout(slideshowTimer_714782);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_714782();
		var speed = slideshowMotionSpeed_714782();
		var currentNum = document.getElementById('currentNum_714782').value;
		var totalDips = document.getElementById('totalDips_714782').value;
		var video = document.getElementById('video_714782').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_714782(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_714782' + nums).length){
				$('#nav_714782' + nums).removeClass('on');
			}
			
			if($('#nav_li_714782' + nums).length){
				$('#nav_li_714782' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_714782' + nums).length){
					$('#videoShell_714782' + nums).hide();
				}
				$('#dipImage_714782' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_714782' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_714782' + nums).length){
				$('#videoShell_714782' + nums).fadeOut(speed);
			}
			if($('#dipContent_714782' + nums).length){
				$('#dipContent_714782' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_714782' + nums).length){
				$('#nav_714782' + nums).addClass('on');
			}
			
			if($('#nav_li_714782' + nums).length){
				$('#nav_li_714782' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_714782' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_714782' + nums).length){
					$('#videoShell_714782' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_714782' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_714782' + nums).length){
					$('#videoShell_714782' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_714782' + nums).length){
				$('#dipContent_714782' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_714782(timer);
			document.getElementById('currentNum_714782').value = nums;
		}
	}
	
	function slideshowMotion_714782(){
