td.script:
function ec_712651(ecid,display){
	var displayDiv = document.getElementById('ec_'+ecid+'_content').style.display;
	if(displayDiv == 'block'){
		document.getElementById('ec_'+ecid+'_content').style.display = 'none';
		document.getElementById('ec_'+ecid+'_minus').style.display = 'none';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'block';
		$('#ec_'+ecid+'_shell').removeClass('expand_collapse_shell_open');
	}else{
		document.getElementById('ec_'+ecid+'_content').style.display = 'block';
		document.getElementById('ec_'+ecid+'_minus').style.display = 'block';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'none';
		$('#ec_'+ecid+'_shell').addClass('expand_collapse_shell_open');
	}
	
	/*document.getElementById('ec_'+ecid+'_content').style.display = display;
	if(display=='block') {
		document.getElementById('ec_'+ecid+'_minus').style.display = 'block';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'none';
	}
	else {
		document.getElementById('ec_'+ecid+'_minus').style.display = 'none';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'block';
	}*/
}

