jQuery(function(){
    jQuery('.navbar-inner.navb ul.nav li a').click(function(e){
       e.stopPropagation();
       jQuery(this).parents('li').toggleClass('openclick open');  
       jQuery('.navbar-inner.navb ul.nav li').not(jQuery(this).parent()).removeClass('openclick open')
    });

    jQuery('ul.nav li ul li a').click(function(e){
       e.stopPropagation();
        jQuery(this).parents('li').toggleClass('openclick open');  
       jQuery(this).parents('li').addClass('opensubmenu');  
       jQuery('ul.nav li ul li').not(jQuery(this).parent()).removeClass('opensubmenu')
    });

    jQuery('html').click(function() {
    jQuery( '.navbar-inner.navb ul.nav li' ).removeClass( 'openclick' ); 
    });

});


