div.script:
    $(document).ready(function() {
		
        	var t = setTimeout(function(){
				var alertHeight =  $('#announEmergencyShell').height();
                $('#announEmergencyShell').slideDown(700,function(){
					$('#announEmergencyShell').css('position', 'fixed');
					$('#alertSpacer ').css('height', alertHeight + 'px').css('display','block');
				});
            },1000);
		
        });

function googleTranslateElementInit() {
   new google.translate.TranslateElement({  
        includedLanguages:'es',          			
        pageLanguage: 'en',
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
        autoDisplay: false
        }, 'google_translate_element');
        $('.goog-te-gadget-simple').css('font-size', '10px');
}
