function resizeSlider_ir4889() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1002");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir4889-ir4889-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir4889-ir4889-slider").style.height = Math.max((parseFloat("590")/parseFloat("1200") * containerWidth), parseFloat("590")) + "px";
	document.getElementById("ir4889-ir4889-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir4889(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir4889(); });
}
