function resize_sp1387() {
	var windowWidth = Math.max(parseFloat("334"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1002");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1387Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1387Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1387Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1387Div").style.height = Math.max((parseFloat("200")/parseFloat("334") * windowWidth), parseFloat("200")) + "px";
