link.script:
$(document).ready(function() {
	// returns true if user is using one of the following mobile browsers
	var ismobile=navigator.userAgent.match(/(iPad)|(iPhone)|(iPod)|(android)|(webOS)/i)
	if(ismobile) {
		$('#trustedBlock .video').hide();
		// $('#trustedBlock .video').css('background-image','url("/assets/images/video-still.jpg")').css('opacity','0.3');
	}
});

window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-62240312-1');
