div.script:
$(document).ready(function () {
    $("#5d47114d-30ad-40ce-98e8-5938c944b895").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#642c6fc3-85c4-4c6b-8116-c53c903be943").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#642c6fc3-85c4-4c6b-8116-c53c903be943').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#642c6fc3-85c4-4c6b-8116-c53c903be943').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#70eab99d-da1c-4119-bc08-0fcb529d7dd8').hide();
    jQuery('#70eab99d-da1c-4119-bc08-0fcb529d7dd8').listSolrResults({
        currentPagePath: '/content/schools/arva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '70eab99d-da1c-4119-bc08-0fcb529d7dd8',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

