img.script:
var jq = jQuery.noConflict(true);
jq(document).ready(function(){
        jq(".countdown-active").TimeCircles({
                "animation": "smooth",
                "bg_width": 1,
                "fg_width": 0.05,
                "text_size": .1,
                "count_past_zero": false,
                "circle_bg_color": "#FFFFFF",
                "time": {
                        "Days": {
                                "text": "Days",
                                "color": "#4B8DC8",
                                "show": true
                        },
                        "Hours": {
                                "text": "Hours",
                                "color": "#4B8DC8",
                                "show": true
                        },
                        "Minutes": {
                                "text": "Minutes",
                                "color": "#4B8DC8",
                                "show": true
                        },
                        "Seconds": {
                                "text": "Seconds",
                                "color": "#4B8DC8",
                                "show": true
                        }
                }
        });
        //rebuild when window size changes
        jq(window).resize(function() {
                jq(".countdown-active").TimeCircles().rebuild();
        });
});

div.script:
if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search this site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}
