body.script:
$(function() {
	$('#topnav').accessibleMegaMenu();
	$('#quicklinks').accessibleMegaMenu();

	$('#news_list').slick({
		slidesToShow: 1,
		adaptiveHeight: true,
		mobileFirst: true,
		responsive: [
			{
				breakpoint: 767,
				settings: {
					slidesToShow: 2
				}
			},
			{
				breakpoint: 1023,
				settings: {
					slidesToShow: 4
				}
			}
		]
	});
});

window.onload = init;
var topnavButton = document.getElementById('topnav_mobile_toggle');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}
function toggleNav(){
	topnavDisplay.classList.toggle("open");
}

