div.script:
var jq = jQuery.noConflict(true);

jq(document).ready(function(){

        jq(".countdown-active").TimeCircles({

                "animation": "smooth",

                "bg_width": 1,

                "fg_width": 0.05,

                "text_size": .1,

                "count_past_zero": false,

                "circle_bg_color": "#0F1E4F",

                "time": {

                        "Days": {

                                "text": "Days",

                                "color": "#ffffff",

                                "show": true

                        },

                        "Hours": {

                                "text": "Hours",

                                "color": "#ffffff",

                                "show": true

                        },

                        "Minutes": {

                                "text": "Minutes",

                                "color": "#ffffff",

                                "show": true

                        },

                        "Seconds": {

                                "text": "Seconds",

                                "color": "#ffffff",

                                "show": true

                        }

                }

        });

        //rebuild when window size changes

        jq(window).resize(function() {

                jq(".countdown-active").TimeCircles().rebuild();

        });

});

body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
