     jQuery(function (q) {
         q(".form_div_7713").validationEngine({
             promptPosition: "topRight"
         });

         q(".form_div_7713 input[lang='Submit']").click(function () {
             if (!$('.form_div_7713').validationEngine('validate')) {
                 return false;
             }
         });

     });
     function CancelValidation() {
         jQuery('#Form').validationEngine('detach');
     }

