(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.8&appId=118431008633294";
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

body.script:
                    (function () {
                        var $newTabLinks = $('a[target="_blank"]');
                        var $newTabHiddenElement = $(' Opens in a new browser tab ');

                        $newTabLinks.attr('rel', 'noopener');
                        $newTabLinks.each(function () {
                            var $this = $(this);
                            if ($this.children('span.new-tab-link-info').length === 0) {
                                $this.append($newTabHiddenElement.clone());
                            }
                        });
                    })();
