link.script:
    var slider = new MasterSlider();

    // adds Arrows navigation control to the slider.
    slider.control('arrows', { autohide: true } );
    slider.control('timebar', { insertTo: '#masterslider', align:"top", color:"#E0202F" });
    slider.control('bullets');

    slider.setup('masterslider', {
        width: 2000,    // slider standard width
        height: 400,   // slider standard height
        space: 1,
        layout: 'fullwidth',
        loop: true,
        preload: 0,
        autoplay: true,
        swipe: true,
        view: 'fade',
        mouseSwipe: false,
		layersMode: 'center'

    });

