				callbacks: {
					open: function() {
						$('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					}
				}
	})

	$('#alert-bar-sums-trigger-13694').magnificPopup({
				type:'inline',
				midClick: true,
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('#abl-13694').addClass('abl-popup-open')
						$('#abl-13694').find('.abl-popup-close-trigger').removeClass('abl-popup-close-trigger-hidden')
						$('#abl-13694').find('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					},
					afterClose: function() {
						$('#abl-13694').removeClass('abl-popup-open mfp-hide').removeAttr('style')
						$('#abl-13694').find('.abl-popup-close-trigger').addClass('abl-popup-close-trigger-hidden')
					}
				}
	})

});

