function headerRightSwitch(){	
	var logo = $('header').find('#logo-area');
	var auxArea = $('header').find('#aux-area');
	var logoText = $('header').find('#logo-text-items');
	var auxMenu = $('header').find('#aux-menu');
	$(logo).insertAfter(auxArea);
	$(logoText).insertAfter(auxMenu);
}

$(document).ready(function() {
	
		$('#slide-show-loader-14825').fadeOut('slow');

		$('#slide-show-14825').css({
		'visibility':'visible'
		}).animate({
			'filter': 'alpha(opacity=100)',
			'opacity': '1'
		}, 500);
		
		$.fn.fadeInElements = function() {
				$(this).find('.slide-show-overlay').delay(250).fadeIn('slow');
				$(this).find('.slide-show-headlines').delay(350).fadeIn('slow');
				$(this).find('.slide-show-caption').delay(400).fadeIn('slow');
		};

		$.fn.fadeOutElements = function() {
				$(this).find('.slide-show-overlay').hide();
				$(this).find('.slide-show-headlines').hide();
				$(this).find('.slide-show-caption').hide();
		};

		var slider14825 = $('#slide-show-14825').bxSlider({
			mode: 'horizontal',
			pause: 6000,
			auto: true,
			speed: 1000,
			autoHover: false,
			pagerCustom: '#slide-show-pager-14825, #slide-show-pager-thumbs-14825',
			nextText: '',
			prevText: '',
			nextSelector: '#slide-show-next-14825',
			prevSelector: '#slide-show-prev-14825',
			
			
			onSlideAfter: function (currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
				$('#slide-show-14825').find('.slide-show-active').removeClass('slide-show-active').fadeOutElements();
									$('ul#slide-show-14825 > li').eq(currentSlideHtmlObject + 1).addClass('slide-show-active').fadeInElements();
							},
			onSliderLoad: function () {
								$('ul#slide-show-14825 > li').eq(1).addClass('slide-show-active').fadeInElements();
							}
			
		});
		
		// ADA Compliance, Keyboard Nav
		$(document).keydown(function(e){
			// Right arrow 
			if (e.keyCode == 39) {
					slider14825.goToNextSlide();
					return false;
			// left arrow
			}	else if (e.keyCode == 37) {
					 slider14825.goToPrevSlide();
					return false;
			}
		});
		

});

		function slideshowRefactoring14825(){
			
			var aspectRatio = 0.4285703125;
			var slideShow = $('#slide-show-14825');
			var slideShowItems = $(slideShow).find('.slide-show-item');
			var SSnewWidth = $('#slide-show-outer-14825').outerWidth();
			
			//console.log('new width = '+SSnewWidth);
			
			// check for custom class for max height setting
			var slideshowParent = $('#slide-show-outer-14825').parent().parent();
			
			if($(slideshowParent).is('[class*="max-height-"]')) {		
				var slideShowItemsMaxHeight = slideshowParent.attr('class').match(/\bmax-height-(\d+)\b/)[1]+'px';
			} else {
				var slideShowItemsMaxHeight = '9999px';
			}
						
			// find the new height based on ratio
			var SSnewHeight =  SSnewWidth * aspectRatio;
			
			//console.log('calculated new height = '+SSnewHeight);
			
			function ssCheckHeight(){
				$(slideShowItems).css({
					'height':Math.round(SSnewHeight)+'px',
					'maxHeight':slideShowItemsMaxHeight
				});	
			}
			
			ssCheckHeight();
					
			// Headline Centering / Middling
			$('.slide-show-headlines').each(function(){
				
				var headlineOverlayHtAdjust = $(this).height()/2;
				
				$(this).css({
				 'marginTop':-headlineOverlayHtAdjust+'px'
				});
			
			});
								
		}
	
		slideshowRefactoring14825();
		
		$(window).on('resize', function () {
			slideshowRefactoring14825();
		}).resize();

function makeGrid4898() {
	var parent = $('#ccm-block-blog-index-grid-4898').parent();
	var gridItems = $(parent).find('.blog-index-grid-item');
	if ($(parent).hasClass('grid-2-across')){
		$(gridItems).removeClass('col-sm-3').addClass('col-sm-6');
	} else if ($(parent).hasClass('grid-3-across')){
		$(gridItems).removeClass('col-sm-3').addClass('col-sm-4');
	} else if ($(parent).hasClass('grid-4-across')){
		$(gridItems).removeClass('col-sm-3').addClass('col-sm-3');
	}
}
makeGrid4898();
