function maxHeightThumbs4898(container, thumbContainer) {
	var listParent = $(container).parent();
	if($(listParent).is('[class*="max-height-"]')) {		
		var thumbsMaxHeight = listParent.attr('class').match(/\bmax-height-(\d+)\b/)[1]+'px';
		$(container).find(thumbContainer).css({
			'maxHeight':thumbsMaxHeight,
			'overflow':'hidden'
		});
	}
}
maxHeightThumbs4898('#ccm-block-blog-index-grid-4898', '.blog-index-grid-item-thumb');

