     function validationFile634(){
        var b = true;
         jQuery(".form_div_634 input[type='file']").filter(".ajaxUpload").each(function(i){
            var fileid =  $(this).attr("id");
            jQuery.ajax({
            async:false,
            url: "/DesktopModules/DNNGo_PowerForms/ajaxValidate.aspx",
            cache: false,
            data: "ModuleID=634&type=AjaxCustomUpload&fieldValue="+jQuery(this).val(),
            success: function(Result){
                        if(Result != "true")
                        {
                        jQuery(".form_div_634 #png_upload_"+fileid).attr("src","/DesktopModules/DNNGo_PowerForms/Resource/images/no.png").show();
                        }else
                        {
                            //jQuery(".form_div_634 #png_upload_"+ fileid).attr("src","/DesktopModules/DNNGo_PowerForms/Resource/images/yes.png").show();
                            jQuery(".form_div_634 #png_upload_"+ fileid).hide();
                        }
                          if( Result != "true") {    b= false; } 
                }
            });
        });
        return b;
     }


   

    jQuery(function ($) {


        
   

        $("input.other_textbox").bind("keyup change",function(){
           var forid = "#" + $(this).data("forid");
           $(forid).val($(forid).data("value")+ $(this).val());
        });

        
       
        $(".form_div_634 input[data-verify='Submit']").click(function () { 

           
             //setgrecaptcha();
         

            if(!validationFile634())
            {
                return false;
            }
             
            $(".form_div_634 .group_title span").addClass("min").removeClass("max");
            $(".form_div_634 div[class='group_inside']").show(function () {
               
            });
       });

        
        if ($(".form_div_634  select.region").is("select") && $(".form_div_634  select.region[data-for]").is("select"))
        {
          

            function loadregions634($region, $country)
            {
                $region.empty();
                $region.append('==Please select==');
                var select_country = $country.val();
                if (select_country !== undefined && select_country != '') {
                     
                    $.getJSON("/DesktopModules/DNNGo_PowerForms/Resource_Service.aspx?Token=country.region&ModuleId=634&PortalId=0&TabId=56&Country=" + encodeURIComponent(select_country), function (data) {
                        $.each(data, function (i, item) {
                            $region.append('' + item.Text + '');
                        });
                    });
                }
            }

           $(".form_div_634  select.region").each(function (i) {
                var $region = $(this);
                var $country = $("#"+ $region.data("for"));
                $country.bind("change", function () {
                    loadregions634($region, $country);
                });
                loadregions634($region, $country);
            });
 
        }
  




        
    


        

           
    });

     jQuery(function (q) {
         var Async_validationEngine634 = setInterval(function(){
        if("function" == typeof $(document).validationEngine){
         q(".form_div_634").validationEngine({
             promptPosition: 'topRight',
             ajaxFormValidationURL: '/DesktopModules/DNNGo_PowerForms/ajaxValidate.aspx?ModuleID=634'
         });

         q(".form_div_634 input[data-verify='Submit']").click(function () {

             if (!$('.form_div_634').validationEngine('validate')) {
                 return false;
             }

             if($("#g-recaptcha-634").is("div") )
             {
                 if ( !$("#g-recaptcha-634").data("GRecaptcha"))
                 {
                     $("#g-recaptcha-msg-634").show("fast",function(){
                         $(this).fadeOut(5000);
                     });
                     return false;
                 }
             }
             
             

         });

         q(".form_div_634 input[data-verify='reset']").click(function () {
             $('.form_div_634').validationEngine('hideAll');
         });

        clearInterval(Async_validationEngine634);
        }
        },100)







     });
