  function createMarker(latlng,name,html,code,icon) {

   //determine icon type
   var rand_zi=Math.floor(Math.random()*2)+1;
   if(icon=='public_schools'){type=iconBlue;}
   if(icon=='charter_public_schools'){type=iconOrange;}
   if(icon=='private_schools'){type=iconRed;}

    var marker = new google.maps.Marker({
        position: latlng, 
        map: map,
        //shadow: shadow,
        icon: type,
       // shape: shape,
       code: code,
        title: name,
        zIndex: rand_zi});


    marker.code = code;    


	 var boxText = html;

		google.maps.event.addListener(marker, "click", function (e) { 
      infobox.setContent(boxText);
      infobox.open(map, marker);
  //map.panTo(latlng);

		});  

   gmarkers.push(marker);  

  } 

 function create_map_boundaries(){
