  function loadMap() {
    var myLatlng = new google.maps.LatLng(0, 0);
    var bounds = new google.maps.LatLngBounds();
    var myOptions = {
      zoom: 10,
      center: new google.maps.LatLng(39.116672, -88.328427),
      mapTypeId: google.maps.MapTypeId.ROADMAP
    }


    map = new google.maps.Map(document.getElementById("map"), myOptions);

    // Close the open infobox on map click
		google.maps.event.addListener(map, 'click', function() {
			infobox.close();         
		});    

    //add boundaries
    create_map_boundaries();   

    //add markers
    addmarkers();
  }  


  // == shows all markers of a particular category, and ensures the checkbox is checked ==
  function map_show_icons() {
    //clear markers
    markerClusterer.clearMarkers();   
    infobox.close();

    for (var i=0; i<gmarkers.length; i++) {  
       try{
           if(document.getElementById(gmarkers[i].code).style.display!="none"){

               markerClusterer.addMarker(gmarkers[i]);
              // 

               gmarkers[i].setVisible(true);  
             }
        }catch(err){}      
    }             

                    markerClusterer.resetViewport();
                    markerClusterer.redraw();
  }
