$(function() {
	// ADDED BY BRENTON KELLY - Case: 04317890
    // SUBMIT SEARCH WHEN ENTER KEY IS PRESSED IN THE NAME OR TITLE FIELDS
	$(".ui-widget.app.staffdirectorydiv #txtName, .ui-widget.app.staffdirectorydiv #txtJobTitle").keydown(function(e) {
    	if(e.keyCode == 13) { // ENTER KEY
        	e.preventDefault();

            $(this).closest(".ui-widget-detail").find(".ui-btn-general-primary.search-button").click();
        }
    });
