        "ChannelBar": function() {
        	$(".gb-channel-bar.calendar a").attr("href", $("#navc-CA a").attr("href"));

        	$(".sw-channel-item").each(function() {
            	if($(".sw-channel-dropdown", this).length && $(".sw-channel-dropdown > li", this).length) {
                	$(this).addClass("has-dropdown");
                }
            });

            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },

        "SetHomeAndCalendarChannels": function() {
        	if($("#navc-HP").hasClass("active")) {
            	$(".gb-channel-bar.home > a").addClass("active");
            }
            if($("#navc-CA").hasClass("active")) {
            	$(".gb-channel-bar.calendar > a").addClass("active");
            }
        },

        "StickyChannelBar": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
        	var _this = this;

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // ADD READ FULL STORY BUTTON TO EACH HEADLINE
            $("[data-region='c'] .ui-widget.app.headlines, [data-region='d'] .ui-widget.app.headlines").each(function() {
                $(".ui-article", this).each(function() {
                    var headlineURL = $("h1 a", this).attr("href");
                    $(".ui-article-description", this).append('Learn More');
                });
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            // CHECK APPS
            $(".ui-widget.app:not(.staffdirectorydiv)").each(function() {
            	// HEADER
            	$(".ui-widget-header :header", this).addClass("cs-app-header");

                // FOOTER
                $(".ui-widget-footer", this).addClass("ui-clear");
                $(".ui-widget-footer .clear", this).remove();
                if($(".ui-widget-footer", this).children().length) {
                    $(".ui-widget-footer", this).addClass("has-children");
                } else {
                	$(this).addClass("no-footer");
                }

                // MORE LINKS AND VIEW CALENDAR LINKS
                if($(".view-calendar-link", this).length || $(".more-link", this).length) {
                	$(".ui-widget-header", this).addClass("has-links").append('
');
                }
                if($(".view-calendar-link", this).length) {
                	$(".cs-app-links", this).append($(".view-calendar-link", this));
                }
                if($(".more-link", this).length) {
                	$(".cs-app-links", this).append($(".more-link", this));
                    $(".ui-read-more", this).remove();
                }
            });

            // MOD HEADLINES
            $("[data-region='c'] .ui-widget.app.headlines .ui-articles > li, [data-region='d'] .ui-widget.app.headlines .ui-articles > li").each(function() {
            	// ADD UNDERLAY CONTAINER
                $(".ui-article", this).addClass("ui-clear").prepend('
');

                // SET IMAGE BACKGROUND AND REMOVE IMAGE
                if($("img[src]", this).length) {
                	$(".cs-overlay-panel", this).css("background", "url(" + $(".ui-article-thumb img", this).attr("src") + ") no-repeat center center/cover");
                } else {
                	$(".cs-overlay-panel", this).addClass("no-image");
                }
                $(".ui-article-thumb", this).remove();

                // ADD ATTRIBUTES FOR CLICK
                $(".ui-article", this).attr({
                	"data-url": $(".ui-article-title a", this).attr("href"),
                    "data-target": $(".ui-article-title a", this).attr("target")
                });

                // ADD UNDERLAY PANEL AND MOVE CONTENT INTO IT
                $(".cs-overlay-panel", this).append($(".ui-article-title", this));
                $(".ui-article .clear", this).remove();
                $(".ui-article", this).children().not($(".cs-overlay-panel", this)).wrapAll('
');

                // REPOSITION THE LEARN MORE LINK
                $(".custom-read-more", this).insertBefore($(".cs-underlay-panel", this));

                // CHECK CAPTION
                if($.trim($(".ui-article-description", this).text()) == "") {
                	$(".ui-article", this).addClass("no-caption");
                    $(".cs-underlay-panel", this).remove();
                }
            });

            // HEADLINE CLICK
            $(document).on("click touchend", "[data-region='c'] .ui-widget.app.headlines .ui-article, [data-region='d'] .ui-widget.app.headlines .ui-article", function(e) {
            	if(e.type == "touchend") {
                	e.preventDefault();
                    e.stopPropagation();

                	if($(this).hasClass("touch-open")) {
                    	var host = ($(this).attr("data-url").indexOf("http") == -1) ? window.location.protocol + "//" + window.location.hostname : "";
						var articleUrl = ($(this).attr("data-url").indexOf("../") > -1) ? host + "/" + $(this).attr("data-url").replace(/\.\.\//g, "") : host + $(this).attr("data-url");

                        if($(this).attr("data-target") == "_blank") {
                            window.open(articleUrl, "_blank");
                        } else {
                            window.location.href = articleUrl;
                        }
                    } else {
                    	$(this).addClass("hover touch-open");
                    }
                } else {
                    if(_this.AllyClick(e)) {
                        var host = ($(this).attr("data-url").indexOf("http") == -1) ? window.location.protocol + "//" + window.location.hostname : "";
						var articleUrl = ($(this).attr("data-url").indexOf("../") > -1) ? host + "/" + $(this).attr("data-url").replace(/\.\.\//g, "") : host + $(this).attr("data-url");

                        if($(this).attr("data-target") == "_blank") {
                            window.open(articleUrl, "_blank");
                        } else {
                            window.location.href = articleUrl;
                        }
                    }
                }
            });

            $(document).on("click touchend keydown", "[data-region='c'] .ui-widget.app.headlines .ui-article-title a, [data-region='d'] .ui-widget.app.headlines .ui-article-title a", function(e) {
            	e.stopPropagation();
            });

            $(document).on("touchend", function(e) {
            	$("[data-region='c'] .ui-widget.app.headlines .ui-article, [data-region='d'] .ui-widget.app.headlines .ui-article").removeClass("hover touch-open");
            });

            // HEADLINE FOCUS
            $("[data-region='c'] .ui-widget.app.headlines .cs-underlay-panel, [data-region='d'] .ui-widget.app.headlines .cs-underlay-panel").focusin(function() {
            	$(this).parent().addClass("hover");
            }).focusout(function() {
            	$(this).parent().removeClass("hover touch-open");
            });

            // CHECK HOMEPAGE COLUMNS
            if($(".hp-column.one .ui-widget.app").length) {
