                        "data-has-videos": "true"
                    });

                    // EXTRACT IFRAMES AND REMOVE EXTRA MARKUP
					var embedCode = $.trim($(".ui-widget-detail .ui-article span", this).html().replace(/\s\s+/g, ""));
        			var extractedIframes = embedCode.match(/<iframe.*?<\/iframe>/ig);
                    $(".ui-widget-detail .ui-article span", this).empty();
                    $.each(extractedIframes, function(inded, iframe) {
                    	$(".ui-widget-detail .ui-article span", thisApp).append(iframe);
                    });

                    // APPLY WRAPPERS
                    $("iframe").each(function() {
                        var videoWidth = $(this).attr("width");
