');
                    $('MoreFewer events').insertAfter($(".gb-overflow-articles", this));
                }
            });

            // BIND MORE BUTTON EVENTS
            $(document).on("click keydown", ".gb-toggle-overflow-events", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // EVENTS ARE COLLAPSED
                    if($(this).attr("data-toggle-state") == "more") {
                    	$(this).attr({
                        	"data-toggle-state": "less",
                            "aria-label": $(this).attr("aria-label").replace("More", "Fewer")
                        });
                        $(this).prev(".gb-overflow-articles").attr("aria-hidden", "false").slideDown(function() {
                        	$(".ui-article-description:first-child .sw-calendar-block-title a", this).focus();
                        });
                    }

                    // EVENTS ARE EXPANDED
                    else {
                    	$(this).attr({
                        	"data-toggle-state": "more",
                            "aria-label": $(this).attr("aria-label").replace("Fewer", "More")
                        });
                        $(this).prev(".gb-overflow-articles").attr("aria-hidden", "true").slideUp();
                    }
                }
            });
        },

