style.script:
$('horizontal-slide ul li').click( function() {
  $('.horizontal-slide ul li').removeClass('selected');
  $(this).addClass('selected');
});
$(document).ready(function () {
    $('.horizontal-slide').mousewheel(function(e, delta) {
        this.scrollLeft -= (delta * 40);
        e.preventDefault();
    });
    
    });

