        window.resizeMiniCalendar20200419105924903 = function () {
        var calId = 'miniCal20200419105924903';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419105924903';
        var calMonthFullId = 'miniCalMonthFull20200419105924903';
        var calMonthShortId = 'miniCalMonthShort20200419105924903';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419105924903';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419105924903';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

