$(".group855209").colorbox({rel:'group855209', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group855209").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery855209 = $("a[rel=group855209]").colorbox({maxWidth: "80%"});
$("a#openGallery855209").click(function(e){
    e.preventDefault();
    $gallery855209.eq(0).click();
});

