body.script:
$(document).ready(function() {

                var language_current = $(".styledSelect").text();
	var short_lang = language_current.substring(0,2);
	$(".styledSelect").text(short_lang);
	$('#header #change_language_form .options li').click(function () {
		var language_current = $(this).text();
		var short_lang = language_current.substring(0,2);
		$(".styledSelect").text(short_lang);
	});
	$('html').addClass('district no-js');

	  });

if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
	$('body').removeClass('desktop');
}
$(document).ready(function() {

    $(window).on('scroll', function() {
				var delay = 0;
				$('.main .col-item').children().each(function(i) {

					var bottom_of_object = $(this).offset().top;
					var bottom_of_window = $(window).scrollTop() + $(window).height();
					if (bottom_of_window > bottom_of_object) {

						var $li = $(this);
						setTimeout(function() {
							$li.addClass('active');
						}, delay += 30);
					}
				});
			});
			$(window).trigger('scroll');

		  /* ---	Equal height function resizeable on window resize ---- */

if ($(window).width() > 768) {
  	        var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
  	        $('#slider .bx-wrapper .bx-viewport, .slider-container img').css( "min-height", bulletinBoardHeight );
}
$(window).on('resize', function () {
  	if ($(window).width() > 768) {
		        var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
		        $('#slider .bx-wrapper .bx-viewport, .slider-container img').css( "min-height", bulletinBoardHeight );
        }
        else {
            $('#slider .bx-wrapper .bx-viewport, .slider-container img').css( "min-height", '90px' );
        }
  });
var slider = 	$('.slider-container').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: true,
		controls: false,
		onSlideAfter: function() {
		     slider.stopAuto();
				  slider.startAuto();

		    $('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
		    if ($(window).width() > 768) {
              var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
              $('#slider .bx-wrapper .bx-viewport, .slider-container img').css( "min-height", bulletinBoardHeight );
            }
		}

	});


	  });
