			$PlayInMode: 1,
			$PlayOutMode: 0
		}

	};
	var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_12358', options);


	function resizeEdlioSlider () {
		var parentWidth = document.getElementById('edlio_slider_container_12358').parentElement.clientWidth;

		if (parentWidth) {
			edlioJssorSlider.$ScaleWidth(parentWidth);
