		        "menuBtnText"           : "PAGES",
		        "siteID"                : "4"
		});

		jsMediaQueries();
		useChannelForSection();
		dynamicContent();
        	addFramesetTranslate();
		modEvents();
        	//DOC set background color
        	$(".sp .ui-widget.app.upcomingevents .ui-article .upcoming-column.left").css("background", "rgba("+hexToRgb('#167CBB')+", 0.1)");
		keyBindings();
        headlineMoreLinkCheck(); //Adam - 11.1.17 - 02969470
     });

     function headlineMoreLinkCheck() {
     	$("#hp-content .hp.row.one .headlines").each(function() {
            if($(".more-link", this).length) {
            	$(this).addClass("has-more-link");
          	}
        });
    }

    function stickyHeader() {
        if($(window).width() >= 1024) { 
          scrolled = $(window).scrollTop();
          if($(".hp").length) {
          	if($(".rs-gallery-images").length) {
          		loc = $(".rs-gallery-images").height()/2-150;
          	} else {
          		loc = $("#gb-header-outer").height();
          	}
          } else {
          	loc = $(".sp #gb-header-outer").height();
          }
          if(scrolled >= loc  && scrollLock == 0) {
              $("#gb-header-outer").hide().addClass("fixed-header");
              $("body").css("padding", loc-"px 0 0 0");
	      $("ul.sw-channel-dropdown").css("top", $("#gb-header-outer").height()+2+"px");
	      $("li.sw-channel-item > a span").css("padding-bottom", $("#gb-header-outer").height()-46+"px");
              $("#gb-header-outer").fadeIn(500);
              scrollLock = 1;
          } else if(scrolled < loc && scrollLock == 1){
              $("body").css("padding", "0");
              $("#gb-header-outer").hide().removeClass("fixed-header").fadeIn(500);
	      $("ul.sw-channel-dropdown").css("top", $("#gb-header-outer").height()+2+"px");
	      $("li.sw-channel-item > a span").css("padding-bottom", $("#gb-header-outer").height()-46+"px");
              scrollLock = 0;
          }

          scrolled2 = $(window).scrollTop() + window.innerHeight; 
          loc2 = $("#gb-page").height();
          if(scrolled2 >= loc2  && scrollLock2 == 0) {
              $("#gb-footer-icons a").each(function(index) {
                  $(this).delay(120 * index).animate({
                      top: 0,
                      opacity: 1
                  }, 850, 'easeOutBack');
              });
              scrollLock2 = 1;	          
          }
        }
    }

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				if($(".hp").length){
					photoGallery();
				}
			case "768":
				if($(".hp").length){
					photoGallery();
				}
			break;
			case "640":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
			case "480":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
			case "320":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
		}
	}	

