	function keyBindings() {
      //SW ACCOUNT DROPDOWN
      $("#cs-mystart-account").on('click', function () {
          $(this).children("#cs-myaccount-list").show();
          $(this).addClass("clicked-state");
          $("#cs-myaccount-list li:first-child a").addClass("active").focus();
      });
      $("#cs-mystart-account, #cs-myaccount-list").hover(function () { 
          $(this).children("#cs-myaccount-list").show();
          $(this).addClass("clicked-state");
          $("#cs-myaccount-list li:first-child a").addClass("active").focus();
      }, function () {
          $(this).children("#cs-myaccount-list").hide();
          $(this).removeClass("clicked-state");
          $("#cs-myaccount-list li a").removeClass("active");
      });
      $(".sw-mystart-account").keydown(function (e) {
          if (e.keyCode == 32 || e.keyCode == 13) { //space/enter/down key
            $(this).addClass("clicked-state");
            $("#cs-myaccount-list").show();
            $("#cs-myaccount-list li:first-child a").addClass("active").focus();
          } else if (e.keyCode == 38) { //up key
          	if($("li a:focus", this).hasClass("first")) {
              $("#cs-mystart-account").children("#cs-myaccount-list").hide();
              $("#cs-mystart-account").removeClass("clicked-state");
              $("#cs-myaccount").blur();            
            } else {
              $("li a.active", this).removeClass("active").parent().prev().find("a").addClass("active");
              $("#cs-mystart-account a.active").focus();
            }   
          } else if (e.keyCode == 40) { //down key
          	if($("li a:focus", this).hasClass("last")) {
              $("#cs-mystart-account").children("#cs-myaccount-list").hide();
              $("#cs-mystart-account").removeClass("clicked-state");
              $("#cs-myaccount").blur();            
            } else {
              $("li a.active", this).removeClass("active").parent().next().find("a").addClass("active");
              $("#cs-mystart-account a.active").focus();
            }  
          } 
      });

      //SW SEARCH
      $(".cs-search-button").on("click", function(e) {
      	e.preventDefault();
      	window.location.href="/site/Default.aspx?PageType=6&SiteID=4&SearchString="+$(this).siblings(".cs-search-input").val();
      });
      $(".cs-search-button").on("keypress", function(e) {
      	e.preventDefault();
        if (e.keyCode == 32 || e.keyCode == 13) {
        	window.location.href="/site/Default.aspx?PageType=6&SiteID=4&SearchString="+$(this).siblings(".cs-search-input").val();
        }
      });
      $(".cs-search-input").on("keypress", function(e) {
        if (e.keyCode == 13) {
          window.location.href="/site/Default.aspx?PageType=6&SiteID=4&SearchString="+$(this).val();
        }
      });

      //SW DROPDOWN
      $(".cs-mystart-dropdown").on('click', function () {
          $(".cs-dropdown", this).show();
          $(".cs-dropdown-list li:first-child a").addClass("active").focus();
      });
      $(".cs-mystart-dropdown").hover(function () { 
          $(".cs-dropdown", this).hide();
      }, function () {
          $(".cs-dropdown", this).hide();
      });
      $(".cs-mystart-dropdown").keydown(function (e) {
      	if (e.keyCode == 32 || e.keyCode == 13) { //space/enter/down key
          e.preventDefault();
		  $(".cs-dropdown", this).show();
          $(".cs-dropdown-list li:first-child a").addClass("active").focus();
        } else if (e.keyCode == 38) { //up key
          e.preventDefault();
          if($("li a:focus", this).hasClass("first")) {
            $(".cs-dropdown", this).hide();         
          } else {
            $("li a.active", this).removeClass("active").parent().prev().find("a").addClass("active");
            $(".cs-dropdown a.active").focus();
          }   
        } else if (e.keyCode == 40) { //down key
          e.preventDefault();
          if($("li a:focus", this).hasClass("last")) {
            $(".cs-dropdown", this).hide();        
          } else {
            $("li a.active", this).removeClass("active").parent().next().find("a").addClass("active");
            $(".cs-dropdown a.active").focus();
          }  
        }         
 	  });          

      //SW CHANNEL BAR
      $("ul.sw-channel-dropdown .mega-menu-sections li").keydown(function (e) {
      	if($(this).hasClass("last")) {
          if (e.keyCode == 39) { //key right
            $(this).parent().siblings(".mega-menu-dropdowns").children().attr("tabindex", 0);
            $(this).parent().siblings(".mega-menu-dropdowns").children(".cs-search-input").attr("tabindex", 0);
            $(this).parent().siblings(".mega-menu-dropdowns").children(".cs-mystart-search").focus();
          } else if (e.keyCode == 40) { //key down
            $(this).parent().siblings(".mega-menu-dropdowns").children().attr("tabindex", 0);
            $(this).parent().siblings(".mega-menu-dropdowns").children(".cs-search-input").attr("tabindex", 0);
            $(this).parent().siblings(".mega-menu-dropdowns").children(".cs-mystart-search").focus();            
          }    
      	}
      });

      $(".cs-mystart-search").keydown(function (e) {
        if (e.keyCode == 40) { //key down
          $(this).siblings(".cs-mystart-dropdown.schoollist").focus();          
        }
      });
      $(".cs-mystart-dropdown.schoollist").keydown(function (e) {
        if (e.keyCode == 39) { //key right
            $(this).siblings(".cs-mystart-search").focus(); 
        } else if (e.keyCode == 40) { //key down
        	$(this).siblings(".cs-mystart-dropdown.translate").focus();          
        }
      });
      $(".cs-mystart-dropdown.translate").keydown(function (e) {
        if (e.keyCode == 39) { //key right
            $(this).siblings(".cs-mystart-dropdown.schoollist").focus(); 
        }
      });
    }

	// GET BROWSER AND VERSION
	function getBrowser(){
		var N = navigator.appName, ua=navigator.userAgent, tem;
		var M = ua.match(/(opera|chrome|safari|firefox|msie)\/?\s*(\.?\d+(\.\d+)*)/i);
		if(M && (tem= ua.match(/version\/([\.\d]+)/i))!= null) M[2] = tem[1];
		M = M ? [M[1], M[2]] : [N, navigator.appVersion, '-?'];
		return [M[0], M[1]];
	}

	var browser = getBrowser();
	var browserName = browser[0].toLowerCase();
	var browserVersion = Math.floor(parseInt(browser[1]));

    function hexToRgb(h){
        var r = parseInt((cutHex(h)).substring(0,2),16);
        var g = parseInt((cutHex(h)).substring(2,4),16);
        var b = parseInt((cutHex(h)).substring(4,6),16);
        return r+', '+g+', '+b;
    };

    function cutHex(h) {
    	return (h.charAt(0)=="#") ? h.substring(1,7):h;
    };

