div.script:
document.addEventListener('DOMContentLoaded', function() {
	var tabpanel_panel = new tabpanel("tabpanel_container", false);
});

body.script:
var topnavButton = 'sitenav_main_mobile_toggle';
var topnavDisplay = 'sitenav_main';


function toggleClass(objectId, classToToggle){
	document.getElementById(objectId).classList.toggle(classToToggle);
}

function clickToggleClassOnOtherObject(clickedObjectId, otherObjectId, classToToggle) {
	document.getElementById(clickedObjectId).onclick = function() { toggleClass(otherObjectId, classToToggle); }
}

window.onload = function() {
	clickToggleClassOnOtherObject(topnavButton, topnavDisplay, "open");
}


window.addEventListener('scroll', function(event) {
    var toggleElement = document.getElementById('header_main');
    var watchHeight = 10;

    if ( window.pageYOffset >= watchHeight ) {
        toggleElement.classList.add('scrolled');
        return;
    } else {
        toggleElement.classList.remove('scrolled');
        return;
    }
});
