hr.script:
$('.mlw-readon-trigger').click(function(evt){



	evt.preventDefault();



	var mlwExpTrig = $(this);

	var mlwExpEllips = $(mlwExpTrig).parent().find('.mlw-ellipsis');

	var mlwExpCont = $(mlwExpTrig).parent().find('.mlw-remaining-text');

	var mlwLessTrig = $(mlwExpCont).find('.mlw-less-trigger');



	$(mlwExpTrig).hide();

	$(mlwExpEllips).hide();



	$(mlwExpCont).fadeIn();



	$(mlwLessTrig).click(function(evt){



		evt.preventDefault();



		$(mlwExpCont).fadeOut();

		$(mlwExpTrig).show();

		$(mlwExpEllips).show();



	});



});

