$(document).ready(function() {
	$('.iac-popup-trigger-1479').magnificPopup({
	type:'image'
	});
});

// Responsive Content Choreography - Always puts the image on top of content
var iacBlock = $('#iac-block-1479');

//console.log(iacContentColumnStyle);

$(window).on('resize', function () {


var iacContent = $(iacBlock).find('.iac-content-column');
var iacImage = $(iacBlock).find('.iac-image-column');

	if ($(window).width() < 960) {
		if($(iacContent).hasClass('iac-align-right')) {

			$(iacContent).insertAfter(iacImage); 

		};

	}
	else {

		if($(iacContent).hasClass('iac-align-right')) {

			$(iacContent).insertBefore(iacImage);

		};

	}

}).resize();

$(window).load(function() {
	
	$('#slide-show-loader-1630').fadeOut('slow');
	
	$('#slide-show-1630').css({
	'visibility':'visible'
	}).animate({
		'filter': 'alpha(opacity=100)',
		'opacity': '1'
	}, 500);
	
	$.fn.fadeInElements = function() {
			$(this).find('.slide-show-overlay').delay(250).fadeIn('slow');
			$(this).find('.slide-show-headlines').delay(350).fadeIn('slow');
			$(this).find('.slide-show-caption').delay(400).fadeIn('slow');
	};
	
	$.fn.fadeOutElements = function() {
			$(this).find('.slide-show-overlay').hide();
			$(this).find('.slide-show-headlines').hide();
			$(this).find('.slide-show-caption').hide();
	};
	$('#slide-show-1630').bxSlider({
		mode: 'horizontal',
		pause: 8000,
		auto: true,
		speed: 1000,
		pagerCustom: '#slide-show-pager-1630',
		nextText: '',
		prevText: '',
		nextSelector: '#slide-show-next-1630',
		prevSelector: '#slide-show-prev-1630',
		
		onSlideAfter: function (currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
		$('#slide-show-1630').find('.slide-show-active').removeClass('slide-show-active').fadeOutElements();
		$('ul#slide-show-1630 > li').eq(currentSlideHtmlObject + 1).addClass('slide-show-active').fadeInElements();
		},
		onSliderLoad: function () {
		$('ul#slide-show-1630 > li').eq(1).addClass('slide-show-active').fadeInElements();
		}
		
	});
});
// Responsively scaling down height of slideshow with preset breakpoints
var slideShow = $('#slide-show-1630');
var slideShowItems = $(slideShow).find('.slide-show-item');

	var SSoriginalWidth = $(slideShowItems).width();
	var SSoriginalHeight = $(slideShowItems).height();
	
	var SSmaxHeightSetting = '520';
	var aspectRatio = 0.5625; 
	
	
	$(window).on('resize', function () {
		
		var SSnewWidth = $(slideShowItems).width();
		
		// find the new height based on 16x9 ratio
		var SSnewHeight =  SSnewWidth * aspectRatio ;
		
		// if the ratio is greater than the original max height, fall back to the max height
		// so we don't end up serving up a ridiculously huge image on a huge monitor.
		// THIS WILL CAUSE CROPPING but it's unavoidable
		
		if (SSnewHeight > SSmaxHeightSetting) {

			$(slideShowItems).css({
				'height':SSmaxHeightSetting+'px'
			});
			
		} else {
			
			$(slideShowItems).css({
				'height':Math.round(SSnewHeight)+'px'
			});
			
		}
		

		
		// Headline Centering / Middling
		$('.slide-show-headlines').each(function(){
			
			var headlineOverlayHtAdjust = $(this).height()/2;
			
			$(this).css({
			 'marginTop':-headlineOverlayHtAdjust+'px'
			});
		
		});

	}).resize();

