$(function(){

	var saveWidgetPosition = function($widget_dom){
		var w_id = $widget_dom.attr('data-tsc-widget-id');
		var form_data = {
			widget_id: w_id,
			width: $widget_dom.prop('style').width,
			height: $widget_dom.prop('style').height,
			position_v: $widget_dom.prop('style').top,
			position_h: $widget_dom.prop('style').left
		};
		$.ajax({
			url: url_pass_query_vars({containerAjax:1, action: 'save_widget_position'}),
			data: form_data,
			type: 'POST',
			dataType: 'JSON',
			success: function(data){
				if(!data.success){
					if(data.error){
						alert(data.error);
					}else{
						alert('An error has occured');
					}
					return;
				}
			},
			error: function(){

			}
		});
	}

	var resizing = false;
	$.fn.tscDraggable = function(){
		$(this).on('mousedown', function(e){
			resizing = false;
			var self = $(this);
			var fromX, fromY;
			var currentX, currentY;
			fromX = e.clientX-parseInt($(self).css('left'));
			fromY = e.clientY-parseInt($(self).css('top'));
			var mouseMove = function(e){
				if(resizing){
					return;
				}
				currentX = e.clientX;
				currentY = e.clientY;
				$(self).css('top', ((fromY-currentY)*-1));
				$(self).css('left', ((fromX-currentX)*-1));
			};
			$(document).bind('mousemove', mouseMove);

			var mouseUp = function(e){
				$(document).unbind('mousemove', mouseMove);
				$(document).unbind('mouseup', mouseUp);
				if(resizing){
					return;
				}

				saveWidgetPosition(self);

			};
			$(document).bind('mouseup', mouseUp);

		});
		return this.each(function() {
		});
	};
	$('.tsc-widget-instance.is-absolute').disableSelection()
	.resizable({
		start: function(){
			resizing = true;
		},
		stop: function(){
			saveWidgetPosition($(this));
		}
	}).tscDraggable({

	});
});
