$(document).ready(function() {
						   					   
	$('#budget-close').click(function() {
	      
	
		
		   if($('#budget-container').hasClass('budget-minimized') == false){
				hideBudgetButton();
		   }
		   else{
				showBudgetButton();
		   } 

	});
	
});

function showBudgetButton() {
	$('#budget-container').removeClass('budget-minimized');
			   $('.budget-link').width(0).height(0).show().animate({
			   width: 150,
			   height: 150,
			   opacity: 1
			   }, {
			   duration: 1000
		   }
                   
		 ); 	
}


function hideBudgetButton()	{
	$('#budget-container').removeClass('budget-minimized');
			  $('.budget-link').animate({
				  width: 0,
				  height: 0,
				  opacity: 0.4
			  }, {
			  duration: 1000,
			  complete: function() {
			  $('.budget-link').hide();
			  $('#budget-container').addClass('budget-minimized');
		
			  } 
			}
		  ); 	
}

