div.script:
window.addEventListener("DOMContentLoaded", function() {
	// when close is clicked, collapse accordion and set a cookie to keep hiding the alert during the requested interval
	jQuery("#accordion-alerts .i").on("click", function(e) {
		e.preventDefault();
		var alertElement = jQuery(this).closest(".alert");
		var expires = getAlertExpiration(alertElement.data("interval"));
		setAlertCookie(alertElement.attr("id"), expires);
		alertElement.slideUp();
	});
});

body.script:
window.addEventListener("DOMContentLoaded", function() {
	var urlSearchParams = new URLSearchParams(window.location.search);
	var todaysDate = (urlSearchParams.get("date") != null ? new Date(Date.parse(urlSearchParams.get("date"))) : new Date());
	jQuery(".alert").each(function() {
		alertID = jQuery(this).attr("id");
		// if today (or spoof date) is between the start and end of the alert, we check whether the alert has been dismissed yet
		if (Date.parse(jQuery(this).data("start")) < Date.parse(todaysDate) && Date.parse(todaysDate) < Date.parse(jQuery(this).data("end"))) {
			checkAlertCookie(alertID, todaysDate);
		}
	});
});
function setAlertCookie(alertID, expires) {
	var cookieString = alertID+"="+expires+"; "+(jQuery("#"+alertID).data("interval") == "session" ? "" : "expires="+expires+"; ")+"path=/";
	document.cookie = cookieString;
}
function clearAlertCookie(alertID) {
	document.cookie = alertID+"=; path=/;";
}
function checkAlertCookie(alertID, todaysDate) {
	var theHash = window.location.hash.substr(1);
	// if url contains "showalerts, clear cookie for this alert
	if (theHash.indexOf("showalerts") != -1) {
		clearAlertCookie(alertID);
	}
	// if alert has not been seen yet (cookie is not set), we show the alert and possibly the modal fade
	if (getAlertCookie(alertID) == "" || getAlertCookie(alertID) < Date.parse(todaysDate)) {
		jQuery("#"+alertID).show();
		if (jQuery("#"+alertID).closest("section").attr("id") == "modal-alerts") {jQuery("#modal-fade, #modal-alerts").fadeIn();}
	}
}
function getAlertCookie(cookieID) {
	var cookieFront = cookieID + "=";
	var cookieArray = document.cookie.split(";");
	for (i=0;i<cookieArray.length;i++) {
		var cookie = cookieArray[i];
		while (cookie.charAt(0) == " ") {cookie = cookie.substring(1);}
		if (cookie.indexOf(cookieFront) == 0) {return cookie.substring(cookieFront.length, cookie.length);}
	}
	return "";
}
function getAlertExpiration(interval) {
	var expires = new Date();
	switch(interval) {
		case "always": expires.setTime(expires.getTime() - 1); break;
		case "day": expires.setTime(expires.getTime() + 1*24*60*60*1000); break;
		case "week": expires.setTime(expires.getTime() + 7*24*60*60*1000); break;
		case "month": expires.setTime(expires.getTime() + 30*24*60*60*1000); break;
		default: expires.setTime(expires.getTime() + 100*365*24*60*60*1000); break;
	}
	return Date.parse(expires.toUTCString());
}
