      if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
      	$('body').removeClass('desktop');
      }
      $(document).ready(function() {

      	$(window).on('scroll', function() {
      		var delay = 0;
      		$('.main .col-item').children().each(function(i) {

      			var bottom_of_object = $(this).offset().top;
      			var bottom_of_window = $(window).scrollTop() + $(window).height();
      			if (bottom_of_window > bottom_of_object) {

      				var $li = $(this);
      				setTimeout(function() {
      					$li.addClass('active');
      				}, delay += 30);
      			}
      		});
      	});
      	$(window).trigger('scroll');


      	$('.slider-container').bxSlider({
      		mode: 'fade',
      		auto: true,
      		autoHover: true,
      		adaptiveHeight: true,
      		controls: false,
      		onSlideAfter: function() {
      			$('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
      		}
      	});
      	$('.news-slider').bxSlider({
      		auto: true,
      		autoHover: true,
			pager: false,
			nextText: 'Next News Headline',
			prevText: 'Previous News Headline'
      	});
		$('.bx-clone a').attr('tabindex', '-1').attr('aria-hidden', 'true');

      });
