div.script:
$(document).ready(function () {
    $("#79cca70f-ab39-4e53-bfee-9e1e02342921").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 10 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#d81d3b63-1e9c-460f-b13d-4a7bdcb2200d").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#d81d3b63-1e9c-460f-b13d-4a7bdcb2200d').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#d81d3b63-1e9c-460f-b13d-4a7bdcb2200d').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#fc245a1a-86a7-4015-ba12-aec544158c65').hide();
    jQuery('#fc245a1a-86a7-4015-ba12-aec544158c65').listSolrResults({
        currentPagePath: '/content/schools/mglva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'fc245a1a-86a7-4015-ba12-aec544158c65',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

